CREATE TABLE ATTACHMENT ( ID NUMBER(19) NOT NULL, FILE_NAME VARCHAR2(100), FILE_PATH VARCHAR2(1000), TYPE NUMBER(5), TYPE_ID NUMBER(19), PRIMARY KEY (ID) );
CREATE TABLE AUTHORITY ( ID NUMBER(19) NOT NULL, DATA_URL VARCHAR2(100) NOT NULL, MENU_CLASS VARCHAR2(50) NOT NULL, MENU_CODE VARCHAR2(50) NOT NULL, MENU_NAME VARCHAR2(50) NOT NULL, PARENT_MENUCODE VARCHAR2(50), SEQUENCE NUMBER(19), PRIMARY KEY (ID) );
CREATE TABLE DEPARTMENT ( ID NUMBER(19) NOT NULL, DEPARTMENT_KEY VARCHAR2(20) NOT NULL, DEPARTMENT_VALUE VARCHAR2(40) NOT NULL, DESCRIPTION VARCHAR2(200), PARENT_DEPARTMENTKEY VARCHAR2(20), PRIMARY KEY (ID) );
CREATE TABLE DICT ( ID NUMBER(19) NOT NULL, DICT_KEY VARCHAR2(20) NOT NULL, DICT_VALUE VARCHAR2(40) NOT NULL, PARENT_DICTKEY VARCHAR2(20), SEQUENCE NUMBER(19), PRIMARY KEY (ID) );
CREATE TABLE INFORMATION ( ID NUMBER(19) NOT NULL, AUTHOR VARCHAR2(40), CONTENT VARCHAR2(4000), REFRESH_TIME DATE, TITLE VARCHAR2(100) NOT NULL, PRIMARY KEY (ID) );
CREATE TABLE MAIL ( ID NUMBER(19) NOT NULL, MESSAGE LONG, RECIPIENT VARCHAR2(1000), SENDER VARCHAR2(30), SUBJECT VARCHAR2(200), PRIMARY KEY (ID) );
CREATE TABLE ROLE_AUTHORITY ( ID NUMBER(19) NOT NULL, MENU_CODE VARCHAR2(50) NOT NULL, ROLE_KEY VARCHAR2(40) NOT NULL, PRIMARY KEY (ID) );
CREATE TABLE ROLE ( ID NUMBER(19) NOT NULL, DESCRIPTION VARCHAR2(200), ROLE_KEY VARCHAR2(40) NOT NULL, ROLE_VALUE VARCHAR2(40) NOT NULL, PRIMARY KEY (ID) );
CREATE TABLE ROLE_PERMISSION ( ROLE_ID NUMBER(19) NOT NULL, PERMISSIONS VARCHAR2(255) );
CREATE TABLE SYS_USER ( ID NUMBER(19) NOT NULL, USER_NAME VARCHAR2(40) NOT NULL, SEX NUMBER(1), EMAIL VARCHAR2(30) NOT NULL, PHONE VARCHAR2(20), BIRTHDAY DATE, DEPARTMENT_KEY VARCHAR2(20), PASSWORD VARCHAR2(100) NOT NULL, STATUS NUMBER(1) NOT NULL, LAST_LOGINTIME TIMESTAMP(6), PRIMARY KEY (ID) );
CREATE TABLE SYSUSER_ROLE ( SYSUSER_ID NUMBER(19) NOT NULL, ROLE_ID NUMBER(19) NOT NULL, PRIMARY KEY (SYSUSER_ID, ROLE_ID) );
